import { Component } from 'react';
import Head from 'next/head'

export default class extends Component {
  state = {
    input: '',
    message: null,
  }

  componentDidMount() {
    // start listening the channel message
    global.ipcRenderer.on('message', this.handleMessage)
  }

  componentWillUnmount() {
    // stop listening the channel message
    global.ipcRenderer.removeListener('message', this.handleMessage)
  }

  handleMessage = (event, message) => {
    // receive a message from the main process and save it in the local state
    this.setState({ message })
  }

  handleChange = event => {
    this.setState({ input: event.target.value })
  }

  handleSubmit = event => {
    event.preventDefault()
    global.ipcRenderer.send('message', this.state.input)
    this.setState({ message: null })
  }

  handleClick = event => {
    event.preventDefault()
    fetch('/api/runapp').then(res => res.json())
    .then(json => {
      console.log(json);
    })
  }

  render() {
    return (
      <div className="test" style={{ padding: '1px', backgroundImage: 'url(/bg.jpg)'}}>
        <Head>
          <title>EYI Automation</title>
          <meta name="viewport" content="initial-scale=1.0, width=device-width" />
          <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0-alpha.6/css/bootstrap.min.css" />
        </Head>
        <h1>Welcome to EYI Automation</h1>

        {this.state.message && <p>{this.state.message}</p>}
        <button onClick={this.handleClick}>Click me</button>

        <form onSubmit={this.handleSubmit}>
          <input type="text" onChange={this.handleChange} />
        </form>

        <style jsx>{`
          h1 {
            color: red;
            font-size: 50px;
            text-align:center;
          }
          .test{
            margin: -10px;
            width: 100vw;
            height: 100vh;
            background-repeat: no-repeat;
            background-size: cover;        
          }
        `}</style>
      </div>
    )
  }
}
