import { Component } from 'react';

export default class extends Component {
  state = {
    input: '',
    message: null,
  }

  componentDidMount() {
    // start listening the channel message
    global.ipcRenderer.on('message', this.handleMessage)
  }

  componentWillUnmount() {
    // stop listening the channel message
    global.ipcRenderer.removeListener('message', this.handleMessage)
  }

  handleMessage = (event, message) => {
    // receive a message from the main process and save it in the local state
    this.setState({ message })
  }

  handleChange = event => {
    this.setState({ input: event.target.value })
  }

  handleSubmit = event => {
    event.preventDefault()
    global.ipcRenderer.send('message', this.state.input)
    this.setState({ message: null })
  }

  handleClick = event => {
    event.preventDefault()
    fetch('/api/runapp').then(res => res.json())
    .then(json => {
      console.log(json);
    })
  }

  render() {
    return (
      <div className="test" style={{ padding: '1px', backgroundImage: 'url(/bg.jpg)'}}>
        <h1>Hello Electron!</h1>

        {this.state.message && <p>{this.state.message}</p>}
        <button onClick={this.handleClick}>Click me</button>

        <form onSubmit={this.handleSubmit}>
          <input type="text" onChange={this.handleChange} />
        </form>

        <style jsx>{`
          h1 {
            color: red;
            font-size: 50px;
          }
          .test{
            margin: -10px;
          }
        `}</style>
      </div>
    )
  }
}
