import { Component } from 'react';
import Head from 'next/head'

export default class extends Component {

  constructor(props) {
    super(props);
    this.handleMouseHover = this.handleMouseHover.bind(this);
    this.state = {
      isHovering: false,
    };
  }

  // handleSubmit = event => {
  //   event.preventDefault()
  //   global.ipcRenderer.send('message', this.state.input)
  //   this.setState({ message: null })
  // }

  handleClick = event => {
    event.preventDefault()
    fetch('/api/runapp').then(res => res.json())
    .then(json => {
      console.log(json);
    })
  }

  handleMouseHover() {
    this.setState(this.toggleHoverState);
  }
  toggleHoverState(state) {
    return {
      isHovering: !state.isHovering,
    };
  }

  render() {
    return (
      <div className="test" style={{ padding: '1px', backgroundImage: 'url(/bg.jpg)'}}>
        <Head>
          <title>My apps</title>
          <meta name="viewport" content="initial-scale=1.0, width=device-width" />
          <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0-alpha.6/css/bootstrap.min.css" />
        </Head>
        <h1>Welcome to My apps</h1>
        <div className="container app-but">
          <div className="row">
            <div className="col-sm">
              <button type="button" 
              onMouseEnter={this.handleMouseHover}
              onMouseLeave={this.handleMouseHover}
              className="btn btn-primary">Run the app</button>
            </div>
            <div className="col-sm">
              <button type="button" className="btn btn-primary">Get help</button>
            </div>
          </div>
        </div>
        {
          this.state.isHovering &&
          <div className="showAlter">
            Hovering right meow! üê±
          </div>
        }
        {/* {this.state.message && <p>{this.state.message}</p>}
        <button onClick={this.handleClick}>Click me</button> */}

        {/* <form onSubmit={this.handleSubmit}>
          <input type="text" onChange={this.handleChange} />
        </form> */}

        <style jsx>{`
          h1 {
            color: white;
            margin-top: 50px;
            font-size: 50px;
            text-align:center;
          }
          .test{
            width: 100vw;
            height: 100vh;
            background-repeat: no-repeat;
            background-size: cover;        
          }
          .app-but{
            margin-top:50px;
            text-align:center;

          }
          .showAlter{
            margin-top:50px;
            padding:100px;
            color:white;
            font-size: 20px;
          }
        `}</style>
      </div>
    )
  }
}
